    var currentNode = ""
    if (selection) {
        console.log(selection)
        selection.map(node => (
            // currentSelection += node + ", "
            currentNode = node
        ));
    }    
    return (
        <Fragment>
            <div className='card grid-1'>
                <small>{selection && data.nodes['id']}</small>
            </div>
            <div>
            <div className="card grid-3">
                <div>
                    <strong>Model:</strong> {model}
