import React, { useState, useContext } from 'react';
import ExperimentContext from '../context/experiment/experimentContext';
import PropTypes from 'prop-types';

const Search = ({ searchExperiments, clearExperiments, showClear, setAlert }) => {
  const experimentContext = useContext(ExperimentContext);

  const [text, setText] = useState('');

  const onSubmit = e => {
    e.preventDefault();
    if (text === '') {
      setAlert('Please enter something', 'light');
    } else {
      experimentContext.searchExperiments(text);
      setText('');
    }
  };

  const onChange = e => setText(e.target.value);

  return (
    <div>
      <form onSubmit={onSubmit} className='form'>
        <input
          type='text'
          name='text'
          placeholder='Search Experiments by Expid or Description...'
          value={text}
          onChange={onChange}
        />
        <input
          type='submit'
          value='Search'
          className='btn btn-dark btn-block'
        />
      </form>
      {showClear && (
        <button className='btn btn-light btn-block' onClick={experimentContext.clearExperiments}>
          Clear
        </button>
      )}
    </div>
  );
}


Search.propTypes = {
  searchExperiments: PropTypes.func.isRequired,
  clearExperiments: PropTypes.func.isRequired,
  showClear: PropTypes.bool.isRequired,
  setAlert: PropTypes.func.isRequired
};

export default Search;
