import React, { Fragment, Component } from "react";
import Spinner from "../layout/Spinner";
import GraphRepresentation from "./GraphRepresentation";
import PropTypes from "prop-types";
import { Link } from "react-router-dom";

export class Experiment extends Component {
  state = {
    loading: false
  };

  componentDidMount() {
    this.props.getExperiment(this.props.match.params.expid);
  }

  static propTypes = {
    loading: PropTypes.bool,
    experiment: PropTypes.object.isRequired,
    getExperiment: PropTypes.func.isRequired
  };

  onSubmit = e => {
    this.setState({ loading: true });
    e.preventDefault();
    this.props.getExperimentGraph(this.props.experiment.expid);
    this.setState({ loading: false });
  };

  cleanParent = () => {
    console.log('Exp clean ')
    this.props.cleanParent();
  };

  render() {
    const {
      expid,
      contents,
      contents_conf,
      contents_pkl,
      contents_plot,
      contents_tmp,
      error_message,
      owner,
      owner_id,
      path,
      time_last_access,
      time_last_mod,
      description,
      version,
      size
    } = this.props.experiment;

    const { loading } = this.props;

    if (loading) return <Spinner />;

    return (
      <Fragment>
        <Link to='/' className='btn btn-light'>
          Back To Search
        </Link>
        {/* Message:{' '} */}
        {/* {error_message.length() === 4 ? (
          <i className='fas fa-check text-success' />
        ) : (
          <i className='fas fa-times-circle text-danger' />
        )} */}
        <div className='card text-center'>
          <div className='badge badge-primary'>
            Owner: {owner_id} {owner}
          </div>
          <div className='badge badge-success'>
            Last Access: {time_last_access}
          </div>
          <div className='badge badge-light'>
            Last Modified: {time_last_mod}
          </div>
        </div>
        <div className='card grid-2'>
          <div className='all-center'>
            {/* <img
              src={avatar_url}
              className='round-img'
              alt=''
              style={{ width: '150px' }}
            /> */}
            <h1>{expid}</h1>
            <p>Description: {description}</p>
            <small>Version: {version}</small>
          </div>
          <div>
            {/* {bio && (
              <Fragment>
                <h3>Bio</h3>
                <p>{bio}</p>
              </Fragment>
            )}
            <a href={html_url} className='btn btn-dark my-1'>
              Visit Github Profile
            </a> */}
            <ul>
              <li>
                {error_message !== "None" && (
                  <Fragment>
                    <strong>Error Message: {error_message} </strong>
                  </Fragment>
                )}
              </li>
              <li>
                {path && (
                  <Fragment>
                    <strong>Path: </strong> {path}
                  </Fragment>
                )}
              </li>

              <li>
                {time_last_access && (
                  <Fragment>
                    <strong>Last Access: </strong> {time_last_access}
                  </Fragment>
                )}
              </li>

              <li>
                {time_last_mod && (
                  <Fragment>
                    <strong>Last Modified: </strong> {time_last_mod}
                  </Fragment>
                )}
              </li>
              <li>
                {size && (
                  <Fragment>
                    <strong>Size: </strong> {size}
                  </Fragment>
                )}
              </li>
              <li>
                {contents && <strong>Contents</strong>}
                <ul>
                  {contents &&
                    contents.map(content => (
                      <li key={content}>
                        - {path}/{content}
                      </li>
                    ))}
                </ul>
              </li>
            </ul>
          </div>
        </div>
        <div className='card grid-1'>
          <form onSubmit={this.onSubmit} className='form'>
            <input
              type='submit'
              value='Show Graph'
              className='btn btn-success btn-block'
            />
          </form>
          {this.props.data.error === true && 
          <p className='danger-color'>{this.props.data.error_message}</p>}
          {this.props.showGraph && (
            <GraphRepresentation
              nodes_prop={this.props.data.nodes}
              edges_prop={this.props.data.edges}
              cleanParent={this.cleanParent}
            />
          )}
        </div>
        <div style={experimentStyle}>
          <div className='card'>
            <strong>CONF contents</strong>
            <ul>
              {contents_conf &&
                contents_conf.slice(0, 10).map(content => (
                  <li key={content}>
                    <small>- {content}</small>
                  </li>
                ))}
            </ul>
          </div>
          <div className='card'>
            <strong>PKL contents</strong>
            <ul>
              {contents_pkl &&
                contents_pkl.slice(0, 10).map(content => (
                  <li key={content}>
                    <small>- {content}</small>
                  </li>
                ))}
            </ul>
          </div>
          <div className='card'>
            <strong>PLOT contents</strong>
            <ul>
              {contents_plot &&
                contents_plot.slice(0, 10).map(content => (
                  <li key={content}>
                    <small>- {content}</small>
                  </li>
                ))}
            </ul>
          </div>
          <div className='card'>
            <strong>TMP contents</strong>
            <ul>
              {contents_tmp &&
                contents_tmp.slice(0, 10).map(content => (
                  <li key={content}>
                    <small>- {content}</small>
                  </li>
                ))}
            </ul>
          </div>
        </div>
      </Fragment>
    );
  }
}

const experimentStyle = {
  display: "grid",
  gridTemplateColumns: "repeat(4, 1fr)",
  gridGap: "1rem"
};

export default Experiment;
