import React, { Fragment, Component } from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Navbar from "./components/layout/Navbar";
import Experiments from "./components/experiment/Experiments";
import Experiment from "./components/experiment/Experiment";
import Search from "./components/experiment/Search";
import Alert from "./components/layout/Alert";
import About from "./components/pages/About";
import axios from "axios";
import "./App.css";

class App extends Component {
  state = {
    experiments: [],
    experiment: {},
    loading: false,
    alert: null,
    data: {}
  };

  // Search Github users
  searchExperiments = async text => {
    this.setState({ loading: true });

    const res = await axios.get(`http://84.88.185.94:8888/search/${text}`);
    //console.log(res.data);
    this.setState({ experiments: res.data.experiment, loading: false });
    //console.log(this.state.experiments);
  };

  // Get single Experiment
  getExperiment = async expid => {
    this.setState({ loading: true });

    const res = await axios.get(`http://84.88.185.94:8888/expinfo/${expid}`);

    this.setState({ experiment: res.data, loading: false });
  };

  getExperimentGraph = async expid => {
    this.setState({ loading: true });
    console.log(expid);
    const res = await axios.get(`http://84.88.185.94:8888/graph/${expid}`);
    this.setState({ data: res.data, loading: false });
  };

  // Clear users from state
  clearExperiments = () => this.setState({ experiments: [], loading: false });

  // Set Alert
  setAlert = (msg, type) => {
    this.setState({ alert: { msg, type } });

    setTimeout(() => this.setState({ alert: null }), 5000);
  };

  render() {
    const { experiments, experiment, loading } = this.state;

    return (
      <Router>
        <div className='App'>
          <Navbar />
          <div className='container'>
            <Alert alert={this.state.alert} />
            <Switch>
              <Route
                exact
                path='/'
                render={props => (
                  <Fragment>
                    <Search
                      searchExperiments={this.searchExperiments}
                      clearExperiments={this.clearExperiments}
                      showClear={experiments.length > 0 ? true : false}
                      setAlert={this.setAlert}
                    />
                    <Experiments loading={loading} experiments={experiments} />
                  </Fragment>
                )}
              />
              <Route exact path='/about' component={About} />
              <Route
                exact
                path='/user/:expid'
                render={props => (
                  <Fragment>
                    <Experiment
                      {...props}
                      getExperimentGraph={this.getExperimentGraph}
                      data={this.state.data}
                      getExperiment={this.getExperiment}
                      experiment={experiment}
                      loading={loading}
                    />
                    <div id='myNetwork' />
                  </Fragment>
                )}
              />
            </Switch>
          </div>
        </div>
      </Router>
    );
  }
}

export default App;

