import React, { Component } from 'react';
import Contacts from './components/contacts';

class App extends Component{

  componentDidMount() {
    fetch('http://84.88.185.94:5002/graph/a22l')
    .then(res => res.json())
    .then((data) => {
      this.setState({ contacts: data['edges'] }),
      this.setState({ nodes : data['nodes'] })
    })
    .catch(console.log)
  }

  state = {
    contacts: [],
    nodes: []
  }

  render() {
    return (
      <Contacts contacts={this.state.contacts} />
    );
  }
}

export default App;
