import {
    SEARCH_EXPERIMENTS,
    SET_LOADING,
    CLEAR_EXPERIMENTS,
    GET_EXPERIMENT,
    GET_GRAPH
} from '../types';

export default (state, action) => {
    switch (action.type) {
        case SEARCH_EXPERIMENTS:
            return {
                ...state,
                experiments: action.payload,
                loading: false,
            };
        case SET_LOADING:
            return {
                ...state,
                loading: true,
            };
        case CLEAR_EXPERIMENTS:
            return {
                ...state,
                experiments: [],
                loading: false,
            };
        case GET_EXPERIMENT:
            console.log(action.payload)
            return {
                ...state,                
                experiment: action.payload,
                loading: true,
                data: {}
            };
        case GET_GRAPH:
            return {
                ...state,
                data: action.payload,
                loading: true,
            };
        default:
            return null;
    };
};
