import React, { Component } from 'react';

class JobMonitor extends Component {
    // const experimentContext = useContext(ExperimentContext);
    // const { pkldata, loadingPkl } = experimentContext;
    componentDidMount(){
        this.props.getExperimentPkl(this.props.experiment.expid);
        if (this.props.experiment.running) {
            this.interval = setInterval(() => this.props.getExperimentPkl(this.props.experiment.expid) , this.props.experiment.updateTime * 1000);
        }

        // if (this.props.rundata){
        //     console.log('DidMount Running.')
        // }
    }

    componentWillUnmount() {
        this.props.cleanJobMonitorData();
        if (this.props.experiment.running) {
            clearInterval(this.interval);
        }        
    }

    render() {
        const { loadingPkl, pkldata } = this.props;
        if (loadingPkl) return <div>Loading...</div>
        if (!pkldata) return <div></div>
        return (
            <div>
                Monitoring Jobs...
            </div>
        );
    }

}

export default JobMonitor
