import React, { useContext, useState } from 'react';
import ExperimentContext from '../context/experiment/experimentContext';

const JobSearcher = () => {

    const experimentContext = useContext(ExperimentContext);
    const { searchJobInGraph, foundNodes, loadingSearchJob, data } = experimentContext;

    const [jobId, setJobId] = useState('');    
    const [currentIndex, setCurrentIndex] = useState('');
    const onChangeId = e => setJobId(e.target.value);


    const onSubmit = e => {
        e.preventDefault();
        searchJobInGraph(jobId);
        setCurrentIndex(0);
    }

    const onClickNav = index => {

    }

    return (
        <div className="row">
            <div className="col-md-4">
                <form onSubmit={onSubmit} className='form'>
                    <div className="input-group input-group-sm">                      
                        <input
                            className="form-control"
                            type='text'
                            name='section'
                            placeholder='Job Name (e.g. fc0_1_CLEAN)'                          
                            onChange={onChangeId}
                        />
                        {/* <input
                            className="form-control"
                            type='text'
                            name='hours'
                            placeholder='Hours'                          
                            onChange={onChangeHour}
                        />                     */}
                        {loadingSearchJob && 
                            <span>Searching...</span>
                        }
                        {!loadingSearchJob && 
                        <div className="input-group-append">
                            <input          
                            type='submit'
                            value='Search by Job Name'
                            className='btn btn-dark btn-sm'                            
                            />
                        </div>                    
                        } 
                        {foundNodes && foundNodes.length > 1 &&
                        <div className="input-group-append">
                            <button className="btn-sm btn-info"></button>
                            <button className="btn-sm btn-info"></button>
                        </div>                            
                        }                       
                    </div>
                    </form>
            </div>               
            <div className="col-md-4 offset-md-4 text-right text-muted">
                {data && 
                    <small>Max children: {data.max_children} | Max parents: {data.max_parents} | Total #Jobs: {data.total_jobs}</small>
                }
            </div>         
        </div>
    )
};

export default JobSearcher;
