import React, { useEffect, useContext } from "react";
import Spinner from "../layout/Spinner";
import { Link } from "react-router-dom";
import ExperimentContext from '../context/experiment/experimentContext';

const Experiment = ({ expidToken }) => {
  const experimentContext = useContext(ExperimentContext); 
  const { timeStampToDate, getExperiment, getExperimentGraph, setAutoUpdatePkl, loading, experiment, enabledGraphSearch, data, startAutoUpdatePkl } = experimentContext;

  useEffect(() => {
    getExperiment(expidToken);
    // eslint-disable-next-line
  }, []);

  const onSubmit = e => {
    e.preventDefault();
    getExperimentGraph(experiment.expid);
  };

  const onJobMonitor = e => {
    e.preventDefault();
    setAutoUpdatePkl(true);
  }

  const onNotJobMonitor = e => {
    e.preventDefault();
    setAutoUpdatePkl(false);

  }

  // const cleanParentLocal = () => {
  //   console.log('Exp clean ')
  //   cleanParent();
  // };

  const {
    expid,
    owner,
    owner_id,
    path,
    time_last_access,
    time_last_mod,
    description,
    version,
    updateTime,
    running,
    error,
    error_message,
    pkl_timestamp,
  } = experiment;

  if (loading)  return <Spinner />;

  return (
    <div>
      <Link to='/' className='btn btn-light'>
        Back To Search
      </Link>    
      <br></br>
      <div className="card">
        <div className='card-header text-center pt-1 pb-0'>
            <h3 className="font-weight-bold">{expid} {running && <span className='text-success'> - RUNNING - </span>}{!running && <span className='text-danger'> - NOT RUNNING - </span>}</h3>
        </div>
        <div className='card-text'>

          <div className='col-3'>
            <div><small>{description}</small></div>
            {error && <pre className="text-danger">{error_message}</pre>}
            <div><span>Version: {version}</span></div>
            <div><small>SleepTime: {updateTime}</small></div>
          </div>

          <div className='col-5'>
            <div className='row'>
              <div className='col-6'><small className='font-weight-bold'>Owner:</small> {owner_id} {owner}</div>
              <div className='col-6'><small>Path: {path}</small></div>
            </div>
            <div className='row'>
              <div className='col-6'><small>Last Modified: {time_last_mod}</small></div>
              <div className='col-6'><small>Last Access: {time_last_access}</small></div>
            </div>
            {pkl_timestamp && <div className='row'>
              <div className='col-12'>
                <small>Pkl: {timeStampToDate(pkl_timestamp)}</small>
              </div>             
            </div>} 
          </div>

          <div className='col-md-4'>

          </div>        
        </div> 
        <div className='card-footer'>
          <div className='row p-1'>
              <div className='col-6'>
                <form onSubmit={onSubmit} className='form'>
                  <input
                    type='submit'
                    value='Show Graph'
                    className='btn btn-info btn-block'
                    disabled={!enabledGraphSearch}
                  />
                </form>
              </div>          
              <div className='col-6'>
                {running && data && !startAutoUpdatePkl &&
                  <form onSubmit={onJobMonitor} className='form'>
                    <input
                      type='submit'
                      value='Start Job Monitor'
                      className='btn btn-dark btn-block'
                      disabled={!enabledGraphSearch}
                    />
                </form>
                }
                {running && data && startAutoUpdatePkl &&
                  <form onSubmit={onNotJobMonitor} className='form'>
                    <input
                      type='submit'
                      value='Stop Job Monitor'
                      className='btn btn-danger btn-block'
                      disabled={!enabledGraphSearch}
                    />
                </form>
                }
              </div>
            </div>
        </div>  
      </div>


    </div>
  );
}


export default Experiment;
