import React, { Component } from 'react';
import Contacts from './components/contacts';
import Nodes from './components/nodes';
import { rejects } from 'assert';
import { reject } from 'q';
//import Graph from './components/graph'


class Graph extends Component {
  render() {
    var rendering = "Rendering data from " + this.props.expid
    var nodes_array = []
    var edges_array = []
    if (typeof this.props.nodes_prop != 'undefined' || typeof this.props.edges_prop != 'undefined'){
      this.props.nodes_prop.map((node) => (
          nodes_array.push({id: node.id, label: node.label, color: { background: node.status_color, border: 'black'}})
      ))
      this.props.edges_prop.map((edge) => {
          edges_array.push({from: edge.from, to: edge.to})
      })
      window.test(nodes_array, edges_array)
    } else {
      rendering = "No data.";

      //window.test(null, null)
    }



    return (
      <h3>{rendering}</h3>
    );

  }
}

