      expid : null,
      contacts: [],
      nodes: [],
      total : [],
      errorMessage : ''
    }

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleSubmit(event) {

    const getGraphData = expid => new Promise((resolves, rejects) => {
      const api = 'http://84.88.185.94:5002/graph/'+expid
