      request.open('GET', api)
      request.onload = () =>
        (request.status === 200)?
        resolves(JSON.parse(request.response).results) :
        reject(Error(request.statusText))      
      request.onerror = (err) => rejects(err)
      request.send()
    })

    getGraphData(this.state.expid).then(
