          Experiment Id:
          <input type="text" value={this.state.expid} onChange={this.handleChange}></input>
        </label>
        <input type="submit" value="Submit"></input>
        </form>
        <h3>{this.state.errorMessage}</h3>
        <Graph nodes_prop={this.state.total.nodes} edges_prop={this.state.total.edges} expid={this.state.expid}/>
