import React, { Component } from "react";
import vis from "vis-network";
import Spinner from "../layout/Spinner";

class GraphRepresentation extends Component {
  componentDidMount() {
    console.log("In Graph");

    let container = document.getElementById("myNetwork");

    var nodes_array = [];
    var edges_array = [];

    if (
      typeof this.props.nodes_prop !== "undefined" ||
      typeof this.props.edges_prop !== "undefined"
    ) {
      this.props.nodes_prop.map(node =>
        nodes_array.push({
          id: node.id,
          label: node.label,
          color: { background: node.status_color, border: "black" }
        })
      );

      this.props.edges_prop.map(edge => {
        edges_array.push({ from: edge.from, to: edge.to });
      });

      var nodes = new vis.DataSet(nodes_array);

      var edges = new vis.DataSet(edges_array);

      var data = {
        nodes: nodes,
        edges: edges
      };

      var options = {
        edges: {
          arrows: {
            to: { enabled: true }
          }
        },
        layout: {
          improvedLayout: true,
          // hierarchical: {
          //   parentCentralization: true,
          //   treeSpacing: 100,
          //   nodeSpacing: 250
          // }
        },
        interaction: {
          dragNodes: true,
          hoverConnectedEdges: true
        },
        physics: {
          enabled: false,
          forceAtlas2Based: {
            gravitationalConstant: -50,
            centralGravity: 0.01,
            springConstant: 0.08,
            springLength: 100,
            damping: 0.4,
            avoidOverlap: 0
          }
        },
        // configure: {
        //   enabled: true,
        //   filter: 'nodes,edges',
        //   container: undefined,
        //   showButton: true
        // }
      };

      var network = new vis.Network(container, data, options);
      //window.test(nodes_array, edges_array);
    }
  }

  componentWillUnmount() {
    this.props.cleanParent();
    // if (typeof this.props.nodes_prop !== 'undefined' || typeof this.props.edges_prop !== 'undefined'){
    //   console.log("Unmounting")
    //   window.clearIt();
    // }
  }

  experimentStyle = {
    height: 1200
  };

  render() {
    // if (this.props.loading) {
    //   console.log("Spinning");
    //   return <Spinner />;
    // }
    // console.log("Not Spinning");
    return <div style={this.experimentStyle} id='myNetwork' />;
  }
}

export default GraphRepresentation;
