import React, { Component } from 'react';
import Contacts from './components/contacts';
import Nodes from './components/nodes';
import Graph from './components/graph'

class App extends Component{

  componentDidMount() {
    fetch('http://84.88.185.94:5002/graph/a22l')
    .then(res => res.json())
    .then((data) => {
      this.setState({ contacts: data['edges'], nodes : data['nodes'], total : data })
    })
    .catch(console.log)
  }

  state = {
    contacts: [],
    nodes: [],
    total : []
  }

  render() {
    return (
      <div>
        <Graph nodes_prop={this.state.nodes} edges_prop={this.state.edges} />
        <Contacts contacts={this.state.contacts} />
        <Nodes nodes={this.state.nodes} /> 
      </div>      
    );
  }
}

export default App;
