import { 
    SET_LOADING,
    GET_EXPERIMENT_STATS,
    CLEAR_STATS } from '../types';

export default (state, action) => {
    switch (action.type) {
        case SET_LOADING:
            return {
                ...state,
                loading: true,
            };
        case GET_EXPERIMENT_STATS:
            return {
                ...state,
                statdata: action.payload,
                loading: false,
            };
        case CLEAR_STATS:
            return {
                ...state,
                statdata: null,
                loading: false,
            };
        default:
            return state;
    }
  };
