import React, { useContext, useState } from 'react';
import ExperimentContext from '../context/experiment/experimentContext';

const JobSearcher = () => {

    const experimentContext = useContext(ExperimentContext);
    const { searchJobInGraph, loadingSearchJob, data } = experimentContext;

    const [jobId, setJobId] = useState('');    
    const onChangeId = e => setJobId(e.target.value);


    const onSubmit = e => {
        e.preventDefault();
        searchJobInGraph(jobId);
    }

    return (
        <div className="row">
            <div className="col-4">
                <form onSubmit={onSubmit} className='form'>
                    <div className="input-group input-group-sm">                      
                        <input
                            className="form-control"
                            type='text'
                            name='section'
                            placeholder='Job Name (e.g. fc0_1_CLEAN'                          
                            onChange={onChangeId}
                        />
                        {/* <input
                            className="form-control"
                            type='text'
                            name='hours'
                            placeholder='Hours'                          
                            onChange={onChangeHour}
                        />                     */}
                        {loadingSearchJob && 
                            <span>Searching...</span>
                        }
                        {!loadingSearchJob && 
                            <div className="input-group-append">
                            <input          
                            type='submit'
                            value='Search by Job Name'
                            className='btn btn-info'                            
                            />
                        </div>                    
                        }                        
                    </div>
                    </form>
            </div>   
            <div className="col-4 offset-col-4">
                <div className='row'>
                    <div className='col-md-4 text-left'>
                        <small>{data.max_children}/{data.max_parents} </small>
                    </div>
                    <div className='col-md-4 offset-md-4 text-right'>
                        <small>{data.total_jobs}</small>
                    </div>
                </div>
            </div>         
        </div>
    )
};

export default JobSearcher;
