import React, { Component } from 'react'
import vis from 'vis-network'

class Graph extends Component {

    componentDidMount() {
      var nodes_array = []
      var edges_array = []
      if (typeof this.props.nodes_prop != 'undefined' || typeof this.props.edges_prop != 'undefined'){
        this.props.nodes_prop.map((node) => (
          nodes_array.push({id: node.id, label: node.label, color: { background: node.status_color, border: 'black'}})
        ))
        this.props.edges_prop.map((edge) => {
            edges_array.push({from: edge.from, to: edge.to})
        })

      }

    }

    render() {
      //var rendering = "Rendering data from " + this.props.expid
      var nodes_array = []
      var edges_array = []
      if (typeof this.props.nodes_prop != 'undefined' || typeof this.props.edges_prop != 'undefined'){
        this.props.nodes_prop.map((node) => (
            nodes_array.push({id: node.id, label: node.label, color: { background: node.status_color, border: 'black'}})
        ))
        this.props.edges_prop.map((edge) => {
            edges_array.push({from: edge.from, to: edge.to})
        })

        var nodes = new vis.DataSet(
          nodes_array
        );

        var edges = new vis.DataSet(
          edges_array
        );

        var data = {
          nodes: nodes,
          edges: edges
        };

        var options = {
          edges: {
            arrows : {
              to : {enabled: true}
            }
          },
          layout: {
            improvedLayout: true,
            hierarchical: {
              parentCentralization: true,
              treeSpacing: 100,
              nodeSpacing: 250,
            },
          },
          interaction: {
            dragNodes: true,
            hoverConnectedEdges: true,
          },
          physics: {
            enabled: false,
          }
        };

        var network = new vis.Network({container: "myNetwork", data: data, options: options});
      };

      return (
        <div id="myNetwork"></div>
      );

    }
  }

export default Graph
