class AutosubmitIndex extends Component {

  constructor(props){
    super(props);
    this.state = {
      expid : null,
    }

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleSubmit(event) {
    console.log(this.state.expid);
    event.preventDefault();
  }

  handleChange(event) {
    this.setState({expid: event.target.value})
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          Experiment Id:
          <input type="text" value={this.state.expid} onChange={this.handleChange}></input>
        </label>
        <input type="submit" value="Submit"></input>
      </form>
    );
  }
}

class App extends Component{

  componentDidMount() {

    //const {expid} = this.props.match.params;
    //console.log(this.props);

    fetch('http://84.88.185.94:5002/graph/a22l')
    .then(res => res.json())
    .then((data) => {
      this.setState({ contacts: data['edges'], nodes : data['nodes'], total : data })
    })
    .catch(console.log)
  }

  state = {
    contacts: [],
    nodes: [],
    total : []
  }

  render() {
    if (typeof this.state.total.nodes != "undefined" && typeof this.state.total.edges != "undefined"){
      return (
        <div>        
          <Graph nodes_prop={this.state.total.nodes} edges_prop={this.state.total.edges} />
          {/* <Contacts contacts={this.state.contacts} />
          <Nodes nodes={this.state.nodes} />  */}
        </div>      
      );
    } else {
      return (
        <div>
          <p>No data</p>
        </div>
      );
    }

  }
}

export default AutosubmitIndex;
