      expid : null,
    }
  }

  handleClick() {
    //alert(this.state);
  }

  handleChange = event => {
    this.setState({ expid : event.target.value });
  };

  render() {
    return (
      <div>
        <React.Fragment>
          <form>
            <label htmlFor="expid">expid</label>
            <input
              type = "text"
              name = "expid"
              value = {this.state.expid}
              onChange = {this.handleChange}
            />
          </form>
          <h3>You are selecting: {this.state.expid}</h3>
        </React.Fragment>
