import React, { useContext, Component } from "react";
// import vis from "vis-network";
import Spinner from "../layout/Spinner";
import ExperimentContext from '../context/experiment/experimentContext';
import vis from "vis";

const GraphNativeRep = () => {

    const experimentContext = useContext(ExperimentContext);    
    const { data, loadingGraph, cleanGraphData, updateSelection } = experimentContext;
    if (loadingGraph) return <Spinner></Spinner>
    if (data === null) {
        return (
            <div className="card-body text-left">
                <p className='lead'>Press <span className='badge badge-info'>Show Graph</span> to see the graph representation of the experiment.</p>
                <p className='lead'>If the experiment is running, press <span className='badge badge-dark'>Start Job Monitor</span> to start a live tracker of the changes on the experiment's jobs.
                    This process will automatically update the graph's nodes colors and show a log of the detected changes.
                </p>
            </div> 
        );
    }

    var nodes_array = [];
    var edges_array = [];

    if (data.nodes !== null || data.edges !== null) {
        data.nodes.map(node => nodes_array.push({
            id: node.id,
            label: node.label,
            color: { background: node.status_color, border: "black" },
            //level: node.priority,
          })
        );

        data.edges.map(edge => 
          edges_array.push({ from: edge.from, to: edge.to })
        );
    }

    var nodes = new vis.DataSet(nodes_array);
    var edges = new vis.DataSet(edges_array);

    const options = {
        edges: {
          arrows: {
            to: { enabled: true }
          }
        },
        layout: {
          improvedLayout: true,
          hierarchical: {

            nodeSpacing: 250,
            edgeMinimization: false,
            blockShifting: false,
            parentCentralization: false,
            direction: 'UD',
            sortMethod: 'directed',
          }
        },
        interaction: {
          dragNodes: true,
          hoverConnectedEdges: true,
          // multiselect: true,
          navigationButtons: true,
        },
        physics: {
          enabled: false,
        },
        nodes: {
          shape: 'dot',
        }
    };

    const graph = {
        nodes : nodes,
        edges : edges,
    };


    class VisNetwork extends Component {

        componentDidMount() {
            var network = new vis.Network(this.refs.myRef, graph, options);
            network.on("click", function(params) {
                console.log(params);
                if (params.nodes){
                    console.log(params.nodes);
                    console.log(params.nodes[0].id);
                    updateSelection(params.nodes[0].id);
                }

            });
        }

        componentWillUnmount() {
            cleanGraphData();
        }

        render() {            
            return (
                <div className='card-body p-0'>                    
                    <div ref="myRef" style={experimentStyle}></div>
                </div>
            );
        }
    }

    return <VisNetwork />;

}

const experimentStyle = {
    height: 600
  };

export default GraphNativeRep;
