import React, { Fragment, Component } from 'react';
import Spinner from '../layout/Spinner';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';

export class Experiment extends Component {  

  componentDidMount() {
    console.log(this.props)
    this.props.getExperiment(this.props.match.params.expid);
  }

  static propTypes = {
    loading: PropTypes.bool,
    experiment: PropTypes.object.isRequired,
    getExperiment: PropTypes.func.isRequired
  };

  render() {
    const {
      expid,
      contents,
      contents_conf,
      contents_pkl,
      contents_plot,
      contents_status,
      contents_tmp,
      error_message,
      owner,
      owner_id,
      path,
      time_last_access,
      time_last_mod,
      description,
      version
    } = this.props.experiment;

    const { loading } = this.props;

    if (loading) return <Spinner />;

    return (
      <Fragment>
        <Link to='/' className='btn btn-light'>
          Back To Search
        </Link>
        {/* Message:{' '} */}
        {/* {error_message.length() === 4 ? (
          <i className='fas fa-check text-success' />
        ) : (
          <i className='fas fa-times-circle text-danger' />
        )} */}
        <div className='card grid-2'>
          <div className='all-center'>
            {/* <img
              src={avatar_url}
              className='round-img'
              alt=''
              style={{ width: '150px' }}
            /> */}
            <h1>{expid}</h1>
            <p>Description: {description}</p>
            <small>Version: {version}</small>
          </div>
          <div>
            {/* {bio && (
              <Fragment>
                <h3>Bio</h3>
                <p>{bio}</p>
              </Fragment>
            )}
            <a href={html_url} className='btn btn-dark my-1'>
              Visit Github Profile
            </a> */}
            <ul>              
              <li>
                {error_message !== "None" && (
                  <Fragment>
                    <strong>Error Message: </strong> {error_message}
                  </Fragment>
                )}
              </li>
              <li>
                {path && (
                  <Fragment>
                    <strong>Path: </strong> {path}
                  </Fragment>
                )}
              </li>

              <li>
                {time_last_access && (
                  <Fragment>
                    <strong>Last Access: </strong> {time_last_access}
                  </Fragment>
                )}
              </li>

              <li>
                {time_last_mod && (
                  <Fragment>
                    <strong>Last Modified: </strong> {time_last_mod}
                  </Fragment>
                )}
              </li>
              <li>
                {contents && (<strong>Contents</strong>)}
                <ul>
                {contents && (                  
                    contents.map((content) => (
                      <li key={content}>
                        - {path}/{content}    
                      </li>
                    ))                
                )}
                </ul>
              </li>
            </ul>
          </div>
        </div>
        <div className='card text-center'>
          <div className='badge badge-primary'>Owner: {owner_id} {owner}</div>
          <div className='badge badge-success'>Last Access: {time_last_access}</div>
          <div className='badge badge-light'>Last Modified: {time_last_mod}</div>
        </div>
      </Fragment>
    );
  }
}

export default Experiment;
