import React, { useContext } from 'react'
import ExperimentContext from '../context/experiment/experimentContext';

const Navigator = () => {

    const experimentContext = useContext(ExperimentContext);
    const { navToLatest, messageNavigator } = experimentContext;

    const WaitingCode = 0;
    const UnknownCode = -2;
    const SuspendedCode = -3;
    const QueueCode = 3;
    const FailedCode = -1;
    const CompletedCode = 5;
    const SubmittedCode = 2;
    const RunningCode = 4;
    const ReadyCode = 1;


    const queueColor = {
        background: 'lightpink'
    };
    const failedColor = {
        background: 'red'
    };
    const completedColor = {
        background: 'yellow'
    };
    const submittedColor = {
        background: 'cyan'
    };
    const runningColor = {
        background: 'green'
    };
    const readyColor = {
        background: 'lightblue'
    };
    const waitingColor = {
        background: 'gray'
    }
    const unknownColor = {
        background: 'white',
        color: 'black'
    }
    const suspendedColor = {
        background: 'orange'
    }


    const onLatest = (statusCode) => e => {
      e.preventDefault();
      navToLatest(statusCode); // Completed
    };


    return (
        <div className="row card-body py-1">
                <div className="col-1 px-1">
                <form onSubmit={onLatest(WaitingCode)} className='form'>
                        <input
                        type='submit'
                        value='Waiting'
                        className='btn btn-block btn-sm' 
                        style={waitingColor}                             
                        />
                </form>
                </div> 
                <div className="col-1 px-1">
                <form onSubmit={onLatest(ReadyCode)} className='form'>
                        <input
                        type='submit'
                        value='Ready'
                        className='btn btn-block btn-sm'  
                        style={readyColor}                            
                        />
                </form>
                </div>  
                <div className="col-1 px-1">
                <form onSubmit={onLatest(SubmittedCode)} className='form'>
                        <input
                        type='submit'
                        value='Submitted'
                        className='btn btn-block btn-sm'  
                        style={submittedColor}                            
                        />
                </form>
                </div>
                <div className="col-1 px-1">
                <form onSubmit={onLatest(QueueCode)} className='form'>
                        <input
                        type='submit'
                        value='Queue'
                        className='btn btn-block btn-sm' 
                        style={queueColor}                             
                        />
                </form>
                </div> 
                <div className="col-1 px-1">
                <form onSubmit={onLatest(RunningCode)} className='form'>
                        <input
                        type='submit'
                        value='Running'
                        className='btn btn-block btn-sm'  
                        style={runningColor}                            
                        />
                </form>
                </div> 
                <div className="col-1 px-1">
                <form onSubmit={onLatest(CompletedCode)} className='form'>
                        <input
                        type='submit'
                        value='Completed'
                        className='btn btn-block btn-sm'  
                        style={completedColor}                            
                        />
                </form>
                </div>   
                <div className="col-1 px-1">
                <form onSubmit={onLatest(FailedCode)} className='form'>
                        <input
                        type='submit'
                        value='Failed'
                        className='btn btn-block btn-sm'  
                        style={failedColor}                            
                        />
                </form>
                </div>  
                <div className="col-1 px-1">
                <form onSubmit={onLatest(SuspendedCode)} className='form'>
                        <input
                        type='submit'
                        value='Suspended'
                        className='btn btn-block btn-sm'  
                        style={suspendedColor}                            
                        />
                </form>
                </div>     
                <div className="col-1 px-1">
                <form onSubmit={onLatest(UnknownCode)} className='form'>
                        <input
                        type='submit'
                        value='Unknown'
                        className='btn btn-block btn-info btn-sm' 
                        style={unknownColor}
                        />


                </form>
                </div>
                <div className="col-3 px-1 text-center">
                    <small>{messageNavigator && messageNavigator}</small>
                </div>        
          </div>
    )
}

export default Navigator;
