export const updateTreeData = (runData, treedata) => {
  //const updatedData = {};
  let currentJobNames = new Map();
  runData.forEach((item) => currentJobNames[item.job_name] = item);
  console.log(currentJobNames);
  console.log(treedata);
  treedata.jobs.forEach((item) => {
    item.status = "WAITING"; 
    item.status_code = 0; 
    item.status_color = "gray"; 
    item.minutes = 0; 
    item.minutes_queue = 0;
    // item.id is job_name
    if (currentJobNames.has(item.id)){
      const sourceInfoJob = currentJobNames[item.id];
      item.status = sourceInfoJob.status; 
      item.status_code = sourceInfoJob.status_code; 
      item.status_color = sourceInfoJob.status_color; 
      item.minutes = sourceInfoJob.queue_time_s; 
      item.minutes_queue = sourceInfoJob.running_time_s;
    }
  });

  //return updatedData;
}

export const updateFancyTree = (runData, fancyTreeObject) => {
  console.log("Updating fancyTree object with run data");
}
