import React, { useContext, useEffect } from 'react';
import ExperimentContext from '../context/experiment/experimentContext';


const OpenRun = ({ expidToken }) => {
    const experimentContext = useContext(ExperimentContext); 
    const { getExperimentRun, experiment, autoUpdateLog } = experimentContext;

    useEffect(() => {
        if (autoUpdateLog) {
            console.log(experiment.updateTime)
            setInterval(() => getExperimentRun(expidToken), 10000)
        }        
        // eslint-disable-next-line
    }, []);

    const onSubmit = e => {
        e.preventDefault();
        getExperimentRun(expidToken);
    };

    return (
        <div className='card grid-1'>
        <form onSubmit={onSubmit} className='form'>
          <input
            type='submit'
            value='Show Running Data'
            className='btn btn-primary btn-block'
            // disabled={!enabledGraphSearch}
          />
        </form>
      </div>
    )
}

export default OpenRun;
