import React, { Fragment, useContext } from "react";
// import vis from "vis-network";
import Spinner from "../layout/Spinner";
import Graph from 'react-graph-vis';
import ExperimentContext from '../context/experiment/experimentContext';

const GraphRepresentation = () => {
  const experimentContext = useContext(ExperimentContext);
  const { data, loadingGraph } = experimentContext;
  //const [current, setCurrent] = useState([]);
  // var data = {
  //   nodes: nodes,
  //   edges: edges
  // };
  var nodes_array = [];
  var edges_array = [];

  if (
    typeof data.nodes_prop !== "undefined" ||
    typeof data.edges_prop !== "undefined"
  ) {
    data.nodes_prop.map(node =>
      nodes_array.push({
        id: node.id,
        label: node.label,
        color: { background: node.status_color, border: "black" }
      })
    );

    data.edges_prop.map(edge => {
      edges_array.push({ from: edge.from, to: edge.to });
    });

    // var nodes = new vis.DataSet(nodes_array);

    // var edges = new vis.DataSet(edges_array);

    //var network = new vis.Network(container, data, options);
    //window.test(nodes_array, edges_array);
  }

  const graph = {
    nodes : nodes_array,
    edges : edges_array
  };


    const options = {
      edges: {
        arrows: {
          to: { enabled: true }
        }
      },
      layout: {
        improvedLayout: true,
        hierarchical: {
          //parentCentralization: true,
          //treeSpacing: 180,
          nodeSpacing: 250,
          edgeMinimization: false,
          blockShifting: false,
          parentCentralization: false,
          direction: 'UD',
          sortMethod: 'directed',
        }
      },
      interaction: {
        dragNodes: true,
        hoverConnectedEdges: true,
        multiselect: true,
        navigationButtons: true,
      },
      physics: {
        enabled: false,
      },
      nodes: {
        shape: 'dot',
      }
      // configure: {
      //   enabled: true,
      //   filter: 'nodes,edges',
      //   container: undefined,
      //   showButton: true
      // }
    };



    const events = {
      select: function(event) {
        var { nodes, edges } = event;
        //setCurrent(nodes);
        console.log("Selected nodes:");
        console.log(nodes);
        //setCurrent(nodes);
        // console.log("Selected edges:");
        // console.log(edges);
      }
    };

    if (loadingGraph) return <Spinner />;

    return (
      <Fragment>
        <Graph
          style={experimentStyle}
          graph={graph}
          options={options}
          events={events}
          getNetwork={network => {
            //  if you want access to vis.js network api you can set the state in a parent component using this property
          }}
        />
        {/* <Selection selectionList={current} /> */}
      </Fragment>
    );  

}

const experimentStyle = {
    height: 800
  };

export default GraphRepresentation;
