import React, { Component } from 'react';


class OpenRun extends Component {

    onSubmit = e => {
        e.preventDefault();
        this.props.getExperimentRun(this.props.experiment.expid);
    };

    onAutoUpdate = e => {
        e.preventDefault();
        this.interval = setInterval(() => this.props.getExperimentRun(this.props.experiment.expid) , this.props.experiment.updateTime);
    };

    componentWillUnmount() {
      clearInterval(this.interval);
    }

    render() {
      return (
        <div className='card grid-1'>
          <form onSubmit={this.onSubmit} className='form'>
            <input
              type='submit'
              value='Show Running Data'
              className='btn btn-primary btn-block'
              // disabled={!enabledGraphSearch}
            />
          </form>
          {/* { this.props.rundata &&  <form onSubmit={this.onAutoUpdate} className='form'>
