import React, { Component } from 'react';
import Contacts from './components/contacts';
import Nodes from './components/nodes';
//import Graph from './components/graph'


class Graph extends Component {
  render() {
    var nodes_array = []
    var edges_array = []
    {this.props.nodes_prop.map((node) => (
        nodes_array.push({id: node.id, label: node.label, color: { background: node.status_color, border: 'black'}})
    ))}
    {this.props.edges_prop.map((edge) => {
        edges_array.push({from: edge.from, to: edge.to})
    })}

    window.test(nodes_array, edges_array)

    return null;

  }
}

class AutosubmitIndex extends Component {

  constructor(props){
    super(props);
    this.state = {
      expid : null,
      contacts: [],
      nodes: [],
      total : [],
      shouldShow : false
    }

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleSubmit(event) {

    fetch('http://84.88.185.94:5002/graph/'+this.state.expid)
    .then(res => res.json())
    .then((data) => {
      this.setState({ contacts: data['edges'], nodes : data['nodes'], total : data })
    })
    .catch(console.log)
    this.setState({shouldShow: true});
    event.preventDefault();
  }

  handleChange(event) {
    this.setState({shouldShow: false});
    this.setState({expid: event.target.value});
  }

  render() {
    if (this.state.shouldShow == true && typeof this.state.total.nodes != "undefined" && typeof this.state.total.edges != "undefined"){
      return(
        <div>
          <form onSubmit={this.handleSubmit}>
          <label>
            Experiment Id:
            <input type="text" value={this.state.expid} onChange={this.handleChange}></input>
          </label>
          <input type="submit" value="Submit"></input>
          </form>

          <Graph nodes_prop={this.state.total.nodes} edges_prop={this.state.total.edges} />
        </div>
      );
    }else{

      return(
        <div>
          <form onSubmit={this.handleSubmit}>
          <label>
            Experiment Id:
            <input type="text" value={this.state.expid} onChange={this.handleChange}></input>
          </label>
          <input type="submit" value="Submit"></input>
          </form>
        </div>
      );
    }   
  }
}

class App extends Component{

  componentDidMount() {

    //const {expid} = this.props.match.params;
    //console.log(this.props);

    fetch('http://84.88.185.94:5002/graph/a22l')
    .then(res => res.json())
    .then((data) => {
      this.setState({ contacts: data['edges'], nodes : data['nodes'], total : data })
    })
    .catch(console.log)
  }

  state = {
    contacts: [],
    nodes: [],
    total : []
  }

  render() {
    if (typeof this.state.total.nodes != "undefined" && typeof this.state.total.edges != "undefined"){
      return (
        <div>        
          <Graph nodes_prop={this.state.total.nodes} edges_prop={this.state.total.edges} />
          {/* <Contacts contacts={this.state.contacts} />
          <Nodes nodes={this.state.nodes} />  */}
        </div>      
      );
    } else {
      return (
        <div>
          <p>No data</p>
        </div>
      );
    }

  }
}

export default AutosubmitIndex;
