import React, { Component } from 'react'

class FileStatus extends Component {

  componentDidMount(){
    this.interval = setInterval(() => this.props.getFileStatus(), 300000); // Every 300 seconds
  }

  componentWillUnmount() {
    this.props.cleanFileStatusData();
    clearInterval(this.interval);
  }

  render() {
    if (this.props.esarchiveStatus){
      const {
        avg_bandwidth,
        avg_latency,
        bandwidth_warning,
        current_bandwidth,
        current_latency,
        datetime,
        error, 
        error_message,
        latency_warning,
        response_time,
        response_warning,
        status,
      } = this.props.esarchiveStatus;
      const badge_style = status === "ONLINE" ? "badge badge-success" : "badge badge-secondary";
      return (
        <small>
          &nbsp;<span className={badge_style}>{status}</span>
        </small>
      )
    } else {
      return (<small><span className="badge badge-secondary">UNKNOWN</span></small>);
    }

  }
}

export default FileStatus
