import React, { useState, useContext } from 'react';
import ExperimentContext from '../context/experiment/experimentContext';
import AlertContext from '../context/alert/alertContext';

const Search = ({ setAlert }) => {
  const experimentContext = useContext(ExperimentContext);
  const alertContext = useContext(AlertContext);

  const [text, setText] = useState('');

  const onSubmit = e => {
    e.preventDefault();
    if (text === '') {
      alertContext.setAlert('Please enter something', 'light');
    } else {
      experimentContext.searchExperiments(text);
      //setText('');
    }
  };

  const onChange = e => setText(e.target.value);

  return (

    <div className="input-group mb-3">
      <form onSubmit={onSubmit} className='form'>
        <input
          className="form-control"
          type='text'
          name='text'
          placeholder='Search Experiments by Expid or Description...'
          value={text}
          onChange={onChange}
        />
        <div className="input-group-append">
          <input          
            type='submit'
            value='Search'
            className='btn btn-outline-dark'
          />
        </div>        
      </form>
      {experimentContext.experiments.length > 0 && (
        <button className='btn btn-light btn-block' onClick={experimentContext.clearExperiments}>
          Clear
        </button>
      )}    
            {/* <input class="form-control" type="text" placeholder="Find By Name...">
            <div class="input-group-append">
                <button class="btn btn-outline-secondary" type="button">Search</button>
            </div> */}
    </div>

    // <div>
    //   <form onSubmit={onSubmit} className='form'>
    //     <input
    //       type='text'
    //       name='text'
    //       placeholder='Search Experiments by Expid or Description...'
    //       value={text}
    //       onChange={onChange}
    //     />
    //     <input
    //       type='submit'
    //       value='Search'
    //       className='btn btn-dark btn-block'
    //     />
    //   </form>
    //   {experimentContext.experiments.length > 0 && (
    //     <button className='btn btn-light btn-block' onClick={experimentContext.clearExperiments}>
    //       Clear
    //     </button>
    //   )}
    // </div>
  );
}

export default Search;
